    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        if (Kiemtrarong() == true) {
            JOptionPane.showMessageDialog(null, "thong tin chua duoc nhap du");
        } else
            try {
            NhanVien nv = new NhanVien(txtHoten.getText(), txtNgaysinh.getText(), txtDiachi.getText(), Nam.isSelected() ? "Nam" : "Nu", cbox1.getSelectedItem().toString(),
                    Float.parseFloat(txtHesoluong.getText()),
                     Integer.parseInt(txtThamnien.getText()), Float.parseFloat(txtLuongcoban.getText()));
            JOptionPane.showMessageDialog(null, "them sv thanh cong");
            nv.hienthi();
        } catch (Exception e) {
        }
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveActionPerformed
        if (Kiemtrarong() == true) {
            JOptionPane.showMessageDialog(null, "chua nhap du thong tin");

        } else {
            try {
                NhanVien nv = new NhanVien(txtHoten.getText(), txtNgaysinh.getText(), txtDiachi.getText(), Nam.isSelected() ? "Nam" : "Nu", cbox1.getSelectedItem().toString(),
                        Float.parseFloat(txtHesoluong.getText()), Integer.parseInt(txtThamnien.getText()), Float.parseFloat(txtLuongcoban.getText()));
                FileWriter fw = new FileWriter("nhanvien.dat", true);
                BufferedWriter bw = new BufferedWriter(fw);
                bw.append(nv.Hoten + "$" + nv.Ngaysinh + "$" + nv.Diachi + "$" + nv.Gioitinh + "$" + nv.getPhongban() + "$"
                        + nv.getHesoluong() + "$" + nv.getThamnien() + "$" + nv.getLuongcoban());
                bw.newLine();
                bw.close();
                fw.close();
                 JOptionPane.showMessageDialog(null, "Luu file thanh cong");
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null,"Luu file that bai");
            }
        }
    }//GEN-LAST:event_btnSaveActionPerformed

