    public boolean Kiemtrarong() {
        if (txtHoten.getText().equals("") || txtNgaysinh.getText().equals("") || txtDiachi.getText().equals("")
                || (!Nam.isSelected() && Nu.isSelected()) || txtHesoluong.getText().equals("") || txtThamnien.getText().equals("")
                || txtLuongcoban.getText().equals("")) {
            return true;
        }
        return false;

    }

